import { useState, useEffect } from 'react';
import './nav.css';

const Nav = ()=>{
    const [counter, setCounter] = useState(1);
    const dropdown = ()=>{
        setCounter(counter+1);
    }
    useEffect(()=>{
        const dd = document.querySelector(".dropdown");
        const f = document.querySelector(".first");
        const s = document.querySelector(".second");
        if(counter%2 === 0){
            s.style.width = "30px";
            f.style.marginBottom = "0";
            s.style.transform = "rotate(45deg)";
            f.style.transform = "rotate(-45deg)";
            dd.style.transform =  "translateY(0vh)";
        }
        if(counter%2 === 1)dd.style.transform =  "translateY(-100vh)";

    },[counter]);

    return(
        <div className="navbar">
            <div className="logo">
                <svg xmlns="http://www.w3.org/2000/svg" width="45" height="45" fill="currentColor" class="bi bi-infinity" viewBox="0 0 16 16">
                    <path d="M5.68 5.792 7.345 7.75 5.681 9.708a2.75 2.75 0 1 1 0-3.916ZM8 6.978 6.416 5.113l-.014-.015a3.75 3.75 0 1 0 0 5.304l.014-.015L8 8.522l1.584 1.865.014.015a3.75 3.75 0 1 0 0-5.304l-.014.015L8 6.978Zm.656.772 1.663-1.958a2.75 2.75 0 1 1 0 3.916L8.656 7.75Z"/>
                </svg>
            </div>
            <div className="ham">
                <span onClick={dropdown}>
                    <div className="first"></div>
                    <div className="second"></div>
                </span>
            </div>
        </div>
    );
}

export default Nav;
