import './nav.css';
import './navBreak.css';

const Nav = ()=>{

    const movement = ()=>{
        document.querySelector('.dropdown ul li:nth-of-type(1)').addEventListener("click",()=>{
            setCounter(counter+1);
        });
        document.querySelector('.dropdown ul li:nth-of-type(2)').addEventListener("click",()=>{
            setCounter(counter+1);
            window.scrollTo({
                top : 700,
                behavior : 'smooth'
            })
        })
        document.querySelector('.dropdown ul li:nth-of-type(3)').addEventListener("click",()=>{
            setCounter(counter+1);
            window.scrollTo({
                top : 1400,
                behavior : 'smooth'
            })
        })
        document.querySelector('.dropdown ul li:nth-of-type(4)').addEventListener("click",()=>{
            setCounter(counter+1);
            window.scrollTo({
                top : 2200,
                behavior : 'smooth'
            })
        })
    }


    return(
        <div className="navbar">
            <div className="logo">
                <svg xmlns="http://www.w3.org/2000/svg" width="45" height="45" fill="currentColor" className="bi bi-infinity" viewBox="0 0 16 16">
                    <path d="M5.68 5.792 7.345 7.75 5.681 9.708a2.75 2.75 0 1 1 0-3.916ZM8 6.978 6.416 5.113l-.014-.015a3.75 3.75 0 1 0 0 5.304l.014-.015L8 8.522l1.584 1.865.014.015a3.75 3.75 0 1 0 0-5.304l-.014.015L8 6.978Zm.656.772 1.663-1.958a2.75 2.75 0 1 1 0 3.916L8.656 7.75Z"/>
                </svg>
            </div>
            <div className="ham" onClick={dropdown}>
                <span>
                    <div className="first"></div>
                    <div className="second"></div>
                </span>
            </div>
        </div>
    );
}

export default Nav;
