import { useState,useEffect } from 'react';
import './customCursor.css';

const CustomeCursor = ()=>{

    const [x, setX] = useState();
    const [y, setY] = useState();
    //const [hover, setHover] = useState();

    window.addEventListener("mousemove",(e)=>{
        setX(e.clientX);
        setY(e.clientY);
    });

    useEffect(()=>{
