import { useState,useEffect } from 'react';
import './customCursor.css';

const CustomeCursor = ()=>{

    const [x, setX] = useState();
    const [y, setY] = useState();
    const [hover, setHover] = useState();

    document.addEventListener("mousemove",(e)=>{
        setX(e.clientX);
        setY(e.clientY);
    });

    useEffect(()=>{
        const pointer = document.querySelector(".circlePointer");
        pointer.style.transform = `translate3d(${x}px, ${y}px, 0)`;
    },[x, y]);
    const ham = document.querySelector(".ham span");
    ham.addEventListener("mouseover",()=>{
        setHover(1);
    });
    ham.addEventListener("mouseout",()=>{
        setHover(0);
    });
    useEffect(()=>{
        const pointer = document.querySelector(".circlePointer");
        if(hover){
            pointer.style.width = "40px";
            pointer.style.height = "40px";
        }else{
            pointer.style.width = "30px";
            pointer.style.height = "30px";
        }
    },[hover]);

    return(
        <div className="circlePointer"></div>
    );
}

export default CustomeCursor;
